let prompt_a_usar = 0; // Valor por defecto

try {
    // Obtención de variables
    const tipo_pedido = $('Toma Detalles Conversacion').item.json.meta.sender.custom_attributes.tipo_pedido;
    let stage_raw = $('Toma Detalles Conversacion').item.json.meta.sender.custom_attributes.stage; // Obtener stage sin parsear primero

    let stage = parseInt(stage_raw); // Intentar parsear stage a entero

    if (isNaN(stage)) {
        stage = 0; // Si stage es NaN después de parseInt (o era null/undefined), establecer a 0
    }

    // Funciones auxiliares para verificar tipo de pedido (manejar si tipo_pedido es null/undefined)
    const esDomicilio = (tipo) => tipo && tipo.toLowerCase().includes('omicilio');
    const esRecoger = (tipo) => tipo && tipo.toLowerCase().includes('ecoger');

    // Lógica para determinar prompt_a_usar basada en stage y tipo_pedido
    if (stage === 0) {
        prompt_a_usar = 0;
    } else if (stage === 1) {
        if (esDomicilio(tipo_pedido)) {
            prompt_a_usar = 1;
        } else if (esRecoger(tipo_pedido)) {
            prompt_a_usar = 2;
        }
    } else if (stage === 2) {
        prompt_a_usar = 3;
    } else if (stage === 3) {
        if (esDomicilio(tipo_pedido)) {
            prompt_a_usar = 4;
        } else if (esRecoger(tipo_pedido)) {
            prompt_a_usar = 4;
        }
    }
    // Para cualquier otro stage o si las condiciones no se cumplen, prompt_a_usar permanece en 0 (valor por defecto)

} catch (error) {
    console.error('Error en el procesamiento:', error.message);
    prompt_a_usar = 4;
}

return { prompt_a_usar };

const days = ['Domingo', 'Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado'];

// Crear fecha con zona horaria de Colombia
const now = new Date().toLocaleString('en-US', { timeZone: 'America/Bogota' });
const colombiaTime = new Date(now);

// Obtener el día como String
const dayIndex = colombiaTime.getDay();
const dayName = String(days[dayIndex]);

// Obtener la hora en formato HH:mm:ss
const hours = String(colombiaTime.getHours()).padStart(2, '0');
const minutes = String(colombiaTime.getMinutes()).padStart(2, '0');
const seconds = String(colombiaTime.getSeconds()).padStart(2, '0');
const timeString = `${hours}:${minutes}:${seconds}`;

// Crear el objeto JSON de salida
const output = {
  json: {
    dia: dayName,
    hora: timeString
  }
};

return output;

